 import Table from 'antd/es/table';
import React, { useEffect, useState, Fragment } from "react";
import { connect } from "react-redux";
import FormComponent from './FormComponent';
import ModalComponent from './ModalComponent';
import * as actions from "../actions/postMessage"; 
import Button from 'antd/es/button';
import {
    EditOutlined,DeleteOutlined
  } from '@ant-design/icons';
const onSelectChange = (selectedRowKeys, selectedRows) => {
    console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
}

const TableComponent = ({...props}) => {
    console.log(props)

    //const {classes, ...props} = props
    const [currentId, setCurrentId] = useState(0)

    useEffect(() => {
        props.fetchAllPostMessages()
    }, [])//DidMount

    const onDelete = id => {
        console.log(id)
        const onSuccess = () => { 
           
    }
    props.deletePostMessage(id,onSuccess)
}

const data= props.postMessageList.map((record, index) => {
    console.log(record,index)
    
    return (record)
})
    const columns = [
       
        {
            title: 'Title',
            dataIndex: 'title',
            key: 'title',
        },
        {
            title: 'Message',
            dataIndex: 'message',
            key: 'message',
        },
       
        {
            title: 'Action',
            dataIndex:'action',
            key: 'action',
            render:(action, record) => (
              
                <span>
                    <ModalComponent  icon={<EditOutlined/>} title="Edit"  {...{ currentId, setCurrentId }}    onClick={() => setCurrentId(record._id)}/>
                    <Button icon={<DeleteOutlined />} type="danger" onClick={() => onDelete(record._id)}>Delete</Button>
                </span>
            ),
        },
    ];

    const rowSelection = {
        onChange: onSelectChange
    }

   

    return (
        <div>
            <Table rowSelection={rowSelection} columns={columns} dataSource={data}  />

        </div>
    );
};

const mapStateToProps = state => ({
    postMessageList: state.postMessage.list
})

const mapActionToProps = {
    fetchAllPostMessages: actions.fetchAll,
    deletePostMessage: actions.Delete
}


export default connect(mapStateToProps, mapActionToProps)(TableComponent);
