import Table from "antd/es/table";
import React, { useEffect, useState, Fragment } from "react";
import { connect } from "react-redux";

import * as actions from "../../Redux/postMessage";
 
import PropTypes from "prop-types";
import {columns} from './TableColumn';

const onSelectChange = (selectedRowKeys, selectedRows) => {
  console.log(
    `selectedRowKeys: ${selectedRowKeys}`,
    "selectedRows: ",
    selectedRows
  );
};

const TableComponent = ({ ...props }) => {
  console.log(props);

  const [currentId, setCurrentId] = useState(0);

  useEffect(() => {
    props.fetchAllPostMessages();
  }, []); //DidMount

  const onDelete = (id) => {
    console.log(id);
    const onSuccess = () => {};
    props.deletePostMessage(id, onSuccess);
  };

  console.log(currentId);

  // const columns = [
  //   {
  //     title: "Title",
  //     dataIndex: "title",
  //     key: "title",
  //   },
  //   {
  //     title: "Message",
  //     dataIndex: "message",
  //     key: "message",
  //   },

  //   {
  //     title: "Action",
  //     dataIndex: "action",
  //     key: "action",
  //     render: (action, record, rowIndex) => (
  //       <span>
  //         <ModalComponent
  //           title="update"
  //           record={record}
  //           index={rowIndex}
  //           currentId={record._id}
  //         >
  //           {/* <FormComponent type="update" record={record} index={rowIndex} /> */}
  //         </ModalComponent>
  //         <Button
  //           icon={<DeleteOutlined />}
  //           type="danger"
  //           onClick={() => onDelete(record._id)}
  //         />
  //       </span>
  //     ),
  //   },
  // ];

  const rowSelection = {
    onChange: onSelectChange,
  };

  
  return (
    <div>
      <Table
 
        rowSelection={rowSelection}
        columns={columns}
        dataSource={props.postMessageList}
        pagination={{ position: "bottomRight", pageSize: 5 }}
      />
    </div>
  );
};

const mapStateToProps = (state) => ({
  postMessageList: state.postMessage.list,
});

const mapActionToProps = {
  fetchAllPostMessages: actions.fetchAll,
  deletePostMessage: actions.Delete,
};

TableComponent.propTypes = {
  data: PropTypes.array,
 
};
export default connect(mapStateToProps, mapActionToProps)(TableComponent);
